from read_Codon2AA import read_Codon2AA
from ProteinTranslation import ProteinTranslation
from ReverseComplement import ReverseComplement

# Works!

# Peptide Encoding Problem: Find substrings of a genome encoding 
# a given amino acid sequence (peptide).
def PeptideEncoding(seq, peptide):
    r = []
    codon2aa = read_Codon2AA('Codon_Table.txt')
    seq = seq.replace('T', 'U')          # DNA to RNA
    
    for i in range(len(seq)-3*len(peptide)+1):
        subseq = seq[i:i+3*len(peptide)]
        rsubseq = ReverseComplement(subseq, 'RNA')
        if ProteinTranslation(subseq) == peptide:
            r.append(subseq.replace('U', 'T'))
        if ProteinTranslation(rsubseq) == peptide:
            r.append(subseq.replace('U', 'T'))
    return r


def test():
    in_ = ['ATGGCCATGGCCCCCAGAACTGAGATCAATAGTACCCGTATTAACGGGTGA','MA']
    out_ = ['ATGGCC', 'GGCCAT', 'ATGGCC']
    assert(sorted(PeptideEncoding(*in_)) == sorted(out_)), 'TEST 1 FAILED'

    in_ = ['', 'KEVFEPHYY']
    out_ = ['AAGGAAGTATTTGAGCCTCATTATTAC','AAAGAGGTGTTTGAACCTCATTACTAT','AAGGAGGTATTTGAACCCCACTATTAC','AAAGAAGTTTTCGAACCACATTATTAC','AAGGAAGTGTTTGAACCTCACTATTAT','AAAGAAGTTTTCGAGCCGCACTACTAC','AAGGAAGTATTCGAACCACATTACTAT','ATAATAATGCGGCTCGAATACTTCCTT','GTAGTAATGGGGCTCGAAAACCTCCTT','GTAGTAATGAGGTTCAAAAACCTCCTT','GTAGTAATGGGGTTCGAAGACTTCCTT','ATAATAGTGAGGCTCAAAAACTTCCTT','ATAGTAATGGGGTTCGAAGACTTCCTT','GTAGTAGTGCGGCTCAAAAACTTCCTT','ATAGTAATGAGGTTCGAAAACCTCTTT','ATAATAATGTGGCTCGAACACTTCTTT','GTAGTAATGGGGCTCAAACACCTCTTT','ATAGTAGTGAGGTTCGAAGACTTCCTT','GTAATAGTGCGGTTCAAAAACTTCCTT','ATAGTAGTGTGGTTCAAATACCTCCTT']
    assert(sorted(PeptideEncoding(*in_)) == sorted(out_)), 'TEST 2 FAILED'


